package org.liu.image;

import org.apache.commons.codec.binary.Base64;
import sun.misc.BASE64Decoder;

import java.io.*;

public class ImageBase64Util {

    /**
     * 将图片转换成Base64编码
     *
     * @param imgFile 待处理图片
     */
    public static String getImgStr(String imgFile) {
        // 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
        InputStream in = null;
        byte[] data = null;
        // 读取图片字节数组
        try {
            in = new FileInputStream(imgFile);
            data = new byte[in.available()];
            in.read(data);
            in.close();
        } catch (IOException e) {
            throw new RuntimeException("图片转base64错误", e);
        }
        return new String(Base64.encodeBase64(data));
    }

    /**
     *      * 对字节数组字符串进行Base64解码并生成图片
     *          不能以data:image/png;base64,开头
     *      * @param imgStr 图片数据
     *      * @param imgFilePath 保存图片全路径地址
     *      * @return
     *     
     */
    public static void generateImage(String imgStr, String imgFilePath) {
        BASE64Decoder decoder = new BASE64Decoder();
        try {
            // Base64解码
            byte[] b = decoder.decodeBuffer(imgStr);
            for (int i = 0; i < b.length; ++i) {
                if (b[i] < 0) {// 调整异常数据
                    b[i] += 256;
                }
            }
            // 生成jpg图片
            OutputStream out = new FileOutputStream(imgFilePath);
            out.write(b);
            out.flush();
            out.close();
        } catch (Exception e) {
            throw new RuntimeException("base64转图片错误", e);
        }
    }

    public static void main(String[] args) {
        String imgStr1 = getImgStr("D:\\funny\\Master.png");
        System.out.println(imgStr1);
        String imgStr = "";
        String imgFilePath = "D:\\funny\\Master1.png";
        generateImage(imgStr, imgFilePath);
    }
}
